        particles.push({
          x: Math.floor(Math.random() * W),
          y: Math.floor(Math.random() * H),
          z: Math.floor(Math.random() * mp),
          d: Math.floor(Math.random() * (4 - 1) + 1),
          r: Math.floor(Math.random() * (120 - 30) + 30),
          o: color
        })

        var p = particles[i]
        tempcanvas.width = tempcanvas.height = p.r * 2
        tempctx.fillStyle = p.o
        tempctx.beginPath()
        tempctx.arc(p.r, p.r, p.r, 0, PI2, false)
        tempctx.closePath()
        tempctx.fill()
        var img = new Image()
        img.src = tempcanvas.toDataURL()
        imgs.push(img)
      }
      function animate () {
        requestAnimationFrame(animate)
        ctx.fillStyle = '#48E4B8'
        ctx.fillRect(0, 0, W, H)
        for (var i = 0; i < mp; i++) {
          var p = particles[i]
          p.y -= p.d
          if (p.y <= -p.r * 4) {
            p.y = 326
            p.x = ~~(Math.random() * W)
          }
          ctx.drawImage(imgs[i], p.x, p.y)
        }
      }
      animate()
    })
  }
}
</script>

